class Solution {
public:
    bool checkStrings(string s1, string s2) {
        int n = s1.length();
        vector<int> arr1(26, 0), arr2(26, 0);
        for(int i = 0; i < n; i+=2){
            arr1[s1[i] - 'a']++;
            arr2[s2[i] - 'a']++;
        }

        vector<int> arr3(26, 0), arr4(26, 0);
        for(int i = 1; i < n; i+=2){
            arr3[s1[i] - 'a']++;
            arr4[s2[i] - 'a']++;
        }
        if(arr1 == arr2 && arr3 == arr4)return true;
        return false;

    }
};
