class Solution {
public:
    bool isFascinating(int n) {
        string s = to_string(n);
        string s1 = to_string(2 * n);
        string s2 = to_string(3 * n);
        string t = s + s1 + s2;
        map<char, int> mp;
        for(auto i : t)mp[i]++;
        if(t.length() == 9 && mp.size() == 9 && mp['0'] == 0){
            return true;
        }
        return false;
    }
};
