class Solution {
public:
    bool checkIfPangram(string sentence) {
        unordered_map<char,int> m;
        for(auto i : sentence){
            m[i]++;
        }


        // string ans = "qwertyuiopasdfghjklzxcvbnm";

        for(char ch = 'a';ch<='z';ch = ch + 1){
            if(m[ch] < 1){
                return false;
            }
        }
        return true;
    }
};
