class Solution {
public:
    bool isValid(string s) {
        stack<char> st;
        for(auto i : s) {
            if(i == 'c') {
                if(st.size() >= 2) {
                    char ch1 = st.top();
                    st.pop();
                    char ch2 = st.top();
                    st.pop();
                    if(ch1 != 'b' || ch2 != 'a') {
                        return false;
                    }
                }
                else {
                    return false;
                }
            }
            else {
                st.push(i);
            }
        }

        return st.size() == 0;
    }
};
