class Solution {
public:
    bool checkAlmostEquivalent(string word1, string word2) {
        vector<int> a1(26, 0);
        vector<int> a2(26, 0);
        for(auto i : word1)a1[i -'a']++;
        for(auto i : word2)a2[i -'a']++;
        for(int i = 0; i <26;i++){
            if(abs(a1[i] - a2[i])> 3)return false;
        }
        return true;
    }
};
