class Solution {
public:
    bool isCircularSentence(string s) {
        if(s.size()){
            char f=s[0];
            char prev=s[0];
            bool isgap=0;
            for(int i=0;i<s.length();i++){
                if(s[i]!=' '){
                    if(isgap){
                        if(prev!=s[i]) return 0;
                    }
                    isgap=0;
                    prev=s[i];
                }else{
                    isgap=1;
                } 
            }
            if(prev!=f) return 0;

        }
        return 1;
    }
};
