class Solution {
public:
    void helper(vector<int> &arr, vector<vector<int>> &st, vector<int> &ds, int idx, int target)
    {
        if(target == 0){
            st.push_back(ds);
            return;
        }     
        for(int i = idx;i < arr.size();i++){
            if(i != idx && arr[i] == arr[i-1]) continue;
            if(arr[i] > target) break;
            ds.push_back(arr[i]);
            helper(arr, st, ds, i + 1, target - arr[i]);
            ds.pop_back();      
        }
    }
    vector<vector<int>> combinationSum2(vector<int> &candidates, int target)
    {
        vector<vector<int>> st;
        vector<int> ds;
        sort(candidates.begin(), candidates.end());
        helper(candidates, st, ds, 0, target);
        return st;
    }
};
