class Solution {
public:
    vector<vector<int>> ans;
    void helper(vector<int> &temp, int k, int n, int curr){
        if(k == 0){
            if(n == 0){
                ans.push_back(temp);
                return;
            }else{
                return;
            }
        }
        if(curr > 9)return;
        helper(temp, k, n, curr + 1);
        temp.push_back(curr);
        helper(temp, k - 1, n - curr, curr + 1);
        temp.pop_back();
    }
    vector<vector<int>> combinationSum3(int k, int n) {
        vector<int> temp;
        helper(temp, k, n, 1);
        return ans;
    }
};
