class Solution {
public:
    void helper(vector<int> &arr, vector<vector<int>> &ans, vector<int> &ds, int target, int sum,int i){
        if(sum == target){
            ans.push_back(ds);
            return;
        }
        if(sum > target) return;
        if(i == arr.size()) return;

        ds.push_back(arr[i]);
        helper(arr,ans,ds,target,arr[i] + sum,i);
        ds.pop_back();
        helper(arr,ans,ds,target,sum,i+1);
    }
    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {
        vector<int> ds;
        vector<vector<int>> ans;
        helper(candidates, ans, ds,target,0,0);
        return ans;
    }
};
