class Solution {
public:
    void helper(vector<vector<int>> &ans, vector<int> &ds,int ind, int start,int k,int n){
        if(ind == k){
            ans.push_back(ds);
            return;
        }
        if(start > n)return;
        for(int i= start;i<=n;i++){
            ds.push_back(i);
            helper(ans,ds,ind + 1,i + 1,k,n);
            ds.pop_back();
        }        
    }
    vector<vector<int>> combine(int n, int k) {
        vector<vector<int>> ans;
        vector<int> ds;
        helper(ans, ds,0,1,k,n);
        return ans;
    }
};
