class Solution {
public:
    int maxPower(string s) {
        int ans = 0, cnt =0;
        char prev = 'A';
        for(auto i : s){
            if(i == prev){
                cnt++;
                ans = max(ans, cnt);
            }else{
                cnt = 1;
                prev = i;
            }
        }
        ans = max(ans, cnt);
        return ans;
    }
};
