class Solution {
public:

    bool isPossible(vector<int>& target) {
        priority_queue<long long> q(target.begin(), target.end());
        long long sum = 0;
        for(auto i : target) {
            sum += i;
        }

        while(sum > 1 and q.top() > sum / 2) {
            long long top = q.top();
            q.pop();
            sum -= top;
            if(sum <= 0 || top <= 1) {
                return false;

            }
            if(sum == 1) {
                return true;
            }
            q.push(top%sum);
            sum += (top % sum);
        }
        return sum == target.size();
    }
};
