class Solution {
public:
    int maxArea(vector<int>& height) {
        int start = 0, end = height.size() - 1;
        int ans = 0;
        while(end > start){
            int area = min(height[start], height[end]) * (end - start);
            ans = max(ans, area);
            if(height[start] < height[end]){
                start++;
            }else{
                end--;
            }
        }
        return ans;
    }
};
