class Solution {
public:
    bool checkSubarraySum(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        long long sum = 0;
        mp[0]=-1;
        for(int i =0;i<nums.size();i++){
            sum+=nums[i];
            int rem  = sum%k;
            if(mp.count(rem)){
                //return true;
                if((i-mp[rem])>=2){
                    return true;
                }
            }
            else {
                mp[rem]=i;
            }
        }
        return false;

    }
};
