class Solution {
public:
    string toHex(int num) {
        if(num == 0)return "0";
        unsigned int n = num;
        string temp = "";
        long long pow = 16;
        while(n > 0){
            int dig = n%16;
            if(dig < 10){
                temp.push_back(dig + '0');
            }else{
                temp.push_back(dig -10 + 'a');
            }
            n /= pow;
        }
        reverse(temp.begin(), temp.end());
        return temp;
    }
};
