class Solution {
public:
    vector<vector<int>> findMatrix(vector<int>& nums) {
        map<int, int> m;
        for(auto i : nums)m[i]++;
        vector<vector<int>> ans;
        while(1){
            vector<int> temp;
            for(auto &i : m){
                if(i.second > 0){
                    i.second--;
                    temp.push_back(i.first);
                }
            }
            if(temp.size() == 0)break;
            ans.push_back(temp);
        }
        return ans;
    }
};
