class Solution {
public:
    int countOrders(int n) {

        const int mod = 1e9 + 7;
        long long ans = 1;
        for(long long i = 1; i <= 2 * n; i++) {
            if(i&1) {
                ans *= i;
            }
            else {
                ans *= i / 2;
            }
            ans %= mod;
        }
        return (ans + mod) % mod;

    }
};
