class Solution {
public:
    string helper(int n, string s){
        if(n == 1)return s;
        string ans = "";
        char prev = s[0];
        int count = 1;
        for(int i = 1; i < s.length();i++){
            if(s[i] == prev){
                count++;
            }else{
                ans.append(to_string(count) + prev);
                count = 1;
            }
            prev = s[i];            
        }
        ans.append(to_string(count) + prev);
        return helper(n - 1, ans);        
    }
    string countAndSay(int n) {
        return helper(n, "1");        
    }
};
