class Solution {
public:
    int digArtifacts(int n, vector<vector<int>>& a, vector<vector<int>>& d) {
        bool vis[1005][1005] = {false};

        for(auto i : d) {
            vis[i[0]][i[1]] = true;
        }
        int cnt =0 ;


        for(auto i : a) {
            int sx = i[0];
            int sy = i[1];
            int ex = i[2];
            int ey = i[3];
            bool ok = true;
            for(int j = sx; j <= ex; j++) {
                for(int k = sy; k <= ey; k++) {
                    if(!vis[j][k]) {
                        ok = false;
                        break;
                    }
                }
            }
            if(ok) cnt++;
        }
        return cnt;
    }
};
