class Solution {
public:
    int countBinarySubstrings(string s) {
        vector<int> arr;
        char prev = s[0];
        int curr = 0;
        for(auto i : s){
            if(i == prev){
                curr++;
            }else{
                arr.push_back(curr);
                curr = 1;
                prev = i;
            }
        }
        arr.push_back(curr);
        int ans = 0;
        for(int i = 0; i < arr.size() - 1;i++){
            ans += min(arr[i], arr[i + 1]);
        }
        return ans;
    }
};
