public:
    int countCollisions(string s) {
        stack<char> st;
        int ans  =0;
        for(auto i : s) {
            if(st.empty()) {
                st.push(i);
            }
            else {
                if(i == 'S') {
                    if(!st.empty()) {
                        while(!st.empty() and st.top() == 'R') 
                        {
                            ans += 1;
                            st.pop();
                        }
                    }
                     st.push(i);                   
                }
                else if(i == 'R') {
                    st.push(i);
                }
                else if(i == 'L') {
                    if(st.empty()) {

                    }
