class Solution {
public:
    int distinctIntegers(int n) {
        vector<int> vec={n};
        unordered_set<int> st={n};
        while(vec.size()){
            vector<int> temp;
            for(int i=1;i<=n;i++){
             for(auto ii:vec){
                 if(ii%i==1&&!st.count(i)){
                     temp.push_back(i);
                     st.insert(i);
                     break;
                 }
             }

            }
             vec=temp;
        }
        return st.size();
    }
};
