class Solution {
public:
    int countElements(vector<int>& nums) {
        int _min = *min_element(nums.begin(), nums.end());
        int _max = *max_element(nums.begin(), nums.end());
        int cnt1 = 0, cnt2 = 0;
        for(auto i : nums){
            if(i == _min)cnt1++;
            if(i == _max)cnt2++;
        }
        return max((int)nums.size() - cnt1 - cnt2, 0);
    }
};
