class Solution {
public:
    int countMatches(vector<vector<string> >& items, string ruleKey, string ruleValue) {
        int cnt=0;
        for(int i=0;i<items.size();i++){
            if((ruleKey == "type" and items[i][0] == ruleValue) || (ruleKey == "color" and items[i][1] == ruleValue) || (ruleKey == "name" and items[i][2] == ruleValue)){
                cnt++;
            }
        }
        return cnt;
    }
};
