class Solution {
public:
    int countLargestGroup(int n) {
        // map<int, int> m;
        int cnt = 0;
        vector<int> v(40, 0);
        for(int i=1;i<=n;i++){
            int x = i;
            int sum = 0;
            while(x){

                sum += x%10;
                x/=10;
            }
            v[sum]++;
        }

        int maxi = *max_element(v.begin(), v.end());

        for(auto i : v){
            if (i == maxi) cnt++;
        }
        return cnt;

    }
};
