class Solution {
public:
    int countDistinctIntegers(vector<int>& nums) {
        int n = nums.size();
        for(int i = 0; i < n; i++){
            int t = nums[i];
            int r = 0;
            while(t > 0){
                r  = r * 10 + t%10;
                t/=10;
            }
            nums.push_back(r);
        }
        return (int)set<int>(nums.begin(), nums.end()).size();
    }
};
