class Solution {
public:

    const int MOD = 1e9 + 7;
    int countHomogenous(string s) {
        char prev = 'A';
        long long cnt = 0, ans = 0;
        for(auto i : s){
            if(i == prev){
                cnt++;
            }else{
                long long temp = ((cnt%MOD) *((cnt + 1)%MOD))%MOD;
                temp/=2;
                ans = ((ans%MOD) + (temp%MOD))%MOD;
                prev = i;
                cnt = 1;
            }
        }
        long long temp = ((cnt%MOD) *((cnt + 1)%MOD))%MOD;
        temp/=2;
        ans = ((ans%MOD) + (temp%MOD))%MOD;
        return ans;
    }
};
