class Solution {
    public int countKDifference(int[] nums, int k) {
        int count = 0;
        int n = nums.length;
        for(int i = 0;i<n;i++){
            for(int j = 0;j<n;j++){
                if(nums[i] - nums[j] == k){
                    count+=1;
                }
            }
        }
        return count;
    }


}
