class Solution {
public:


    int countNumbersWithUniqueDigits(int n) {
        int dp[10];
        dp[0] = 1;

        for(int i=1;i<=8;i++) {
            int x = 9;
            int ans = 9;
            int temp = i-1;
            while(temp--) {
                ans *= x;
                x--;
            }
            dp[i] = dp[i-1] + ans;
        }
        return dp[n];
    }
};
