class Solution {
public:
    vector<int> bit;
    int n = 1e5;
    int diff = 1e4 + 5;
    vector<int> countSmaller(vector<int>& nums) {
        bit.assign(n + diff + 1, 0);

        vector<int> ans;
        for(int i = nums.size()-1; i >=0;i--){
            ans.push_back(sum(nums[i] + diff - 1));
            update(nums[i] + diff, 1);
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
    void update(int i, int val){
        while(i <= n){
            bit[i] += val;
            i += (i & (-i));
        }
    }
    int sum(int i){
        int ans = 0;
        while(i > 0){
            ans += bit[i];
            i -= (i & (-i));
        }
        return ans;
    }    
};
