class Solution {
public:
    int countPrefixes(vector<string>& words, string s) {
        int ans=0;
        for(int i=0;i<words.size();i++){
            if(s.length()>=words[i].length()){
                int k=0;
                bool isvalid=1;
                while(k<words[i].length()){
                    if(words[i][k]!=s[k]){
                        isvalid=0;
                        break;
                    }
                    k++;
                }
               if(isvalid) ans++; 
            }
        }
        return ans;
    }
};
