class Solution {
public:
    int countPrimes(int n) {
        if(n < 2)return 0;
        vector<bool> arr(n + 1, true);
        arr[0] = false;
        arr[1] = false;
        for(int i = 2;i<n;i++){
            if(!arr[i])continue;
            for(int j = 2 * i; j < n; j+= i){
                arr[j]  = false;
            }
        }
        int cnt = 0;
        for(int i = 0;i < n;i++){
            if(arr[i])cnt++;
        }
        return cnt;
    }
};
