int dp[55][6];
class Solution {
public:
    int helper(int n, int prev){
        if(n == 0)return 1;
        if(dp[n][prev] != -1)return dp[n][prev];
        int ans = 0;
        for(int i = prev; i < 5;i++){
            ans += helper(n - 1, i);
        }
        return dp[n][prev] = ans;
    }
    int countVowelStrings(int n) {
        memset(dp, -1, sizeof(dp));
        return helper(n, 0);
    }
};
