class Solution {
public:
void dfs(int i,int j,vector<vector<int>> &grid1,vector<vector<int>> &grid2,int &flag){
    if(i>=0 && j>=0 && i<grid2.size() && j<grid2[0].size() && grid2[i][j]==1){
        grid2[i][j]=2;
        if(grid1[i][j]==0){
            flag=0;
        }
        dfs(i+1,j,grid1,grid2,flag);
        dfs(i-1,j,grid1,grid2,flag);
        dfs(i,j+1,grid1,grid2,flag);
        dfs(i,j-1,grid1,grid2,flag);

    }
}
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2){
        int res=0;
        for(int i =0;i<grid2.size();i++){
            for(int j =0;j<grid2[0].size();j++){
                if(grid2[i][j]==1 && grid2[i][j]==grid1[i][j]){
                    int flag =1;
                    dfs(i,j,grid1,grid2,flag);
                    res+=flag;
                }
            }
        }
        return res;
    }
};
