class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        long long ans = 0, x = -1, y = -1, bad = -1;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] == minK){
                x = i;
            }
            if(nums[i] == maxK){
                y = i;
            }
            if(nums[i] > maxK || nums[i] < minK){
                bad = i;
            }
            ans += max(0LL, min(x, y) - bad);
        }
        return ans;
    }
};
