class Solution {
public:

    int countSubstrings(string s, string t) {
        int ans = 0;
        for(int i  = 0; i < s.size(); i++) {
            for(int j = 0; j < t.size(); j++) {
                if(s[i] != t[j]) {
                    int left = 0;
                    int right = 0;
                    for(int k = i - 1, l = j - 1; k >= 0 and l >= 0; k--, l--) {
                        if(s[k] != t[l]) {
                            break;
                        }
                        left++;
                    }
                    for(int k = i + 1, l = j + 1; k < s.size() and l < t.size(); k++, l++) {
                        if(s[k] != t[l]) {
                            break;
                        }
                        right++;
                    }

                    ans += (left + 1) * (right + 1);
                }
            }
        }
        return ans;
    }
};
