class Solution {
public:
    bool check(string s){
        if(s.length()%2)return false;
        int sum1 = 0, sum2 = 0;
        for(int i = 0; i < s.length()/2; i++){
            sum1 += s[i] - '0';
            sum2 += s[s.length() - i - 1] - '0';
        }
        return sum1 == sum2;
    }
    int countSymmetricIntegers(int low, int high) {
        int ans = 0;
        for(int i = low; i <= high; i++){
            if(check(to_string(i))){
                ans++;
            }
        }
        return ans;
    }
};
