class Solution {
public:
    int countConsistentStrings(string allowed, vector<string>& words) {
        int cnt = words.size();
        int freq[26] = {0};
       for(auto c : allowed){
           freq[c-'a']++;
       }


        for(auto c : words){
            for(auto x : c){
                if(freq[x-'a'] == 0){
                    cnt--;
                    break;
                }
            }
        }
        return cnt;
    }
};
