class Solution {
public:
    int vowelStrings(vector<string>& words, int left, int right) {
        int ans = 0;
        for(int i = left; i <=right;i++){
            string s = words[i];
            int t = int(s.length()) - 1;
            if((s[0] == 'a' || s[0] == 'e' || s[0] == 'i'|| s[0] == 'o' || s[0] == 'u') && (s[t] == 'a' || s[t] == 'e' || s[t] == 'i'|| s[t] == 'o' || s[t] == 'u')){
                cout<<s<<" ";
                ans++;
            }
        }
        return ans;
    }
};
