class Solution {
public:
    int countTriplets(vector<int>& arr) {
        for(int i = 1; i < arr.size();i++){
            arr[i] = (arr[i] ^ arr[i-1]);
        }
        int ans = 0;
        for(int i = 0; i  < arr.size();i++){
            for(int j = i + 1; j < arr.size();j++){
                for(int k = j; k < arr.size();k++){
                    int a = arr[j-1];
                    if(i != 0)a = (a ^ arr[i-1]);
                    int b = (arr[k] ^ arr[j-1]);
                    if(a == b){
                        ans++;
                    }
                }
            }
        }
        return ans;
    }
};
