class Solution {
public:
    bool check(char c){
        if(c=='a'||c=='e'||c=='i'||c=='o'||c=='u') return 1;
        return 0;
    }
    vector<int> vowelStrings(vector<string>& words, vector<vector<int>>& queries) {
        vector<int> vec(words.size()+1,0);
        int k=1;
        for(auto i:words){
            if(check(i[0])&&check(i.back())){
                vec[k]=1;
            }else vec[k]=0;
            k++;
        }
        for(int i=1;i<vec.size();i++){
            vec[i]+=vec[i-1];
        }

        vector<int>ans;
        for(auto i:queries){
            // cout<<vec[i[1]]<<" "<<vec[i[0]-1]
            ans.push_back(vec[i[1]+1]-vec[i[0]]);
        }
        return ans;
    }
};
