class Solution {
public:
    int countVowelSubstrings(string word) {
        long long ans = 0;
        for(int i = 0; i < word.size();i++){
            vector<int> temp(26, 0);
            for(int j = i; j < word.size();j++){
                temp[word[j]-'a']++;
                int sum = accumulate(temp.begin(), temp.end(), 0);
                int a = temp['a' - 'a'];
                int e = temp['e' - 'a'];
                int it = temp['i' - 'a'];
                int o = temp['o' - 'a'];
                int u = temp['u' - 'a'];
                if(a > 0 && e>0 && it >0 && o>0 && u>0 && 
                (a + e + it + o + u) == sum){
                    ans++;
                }
            }
        }
        return ans;
    }
};
