class Solution {
public:
    const long long MOD = (long long) 1e9 + 7;



    long long countVowelPermutation(long long n) {
        long long dp[n + 3][30];
        memset(dp, 0, sizeof dp);

        string s = "aeiou";

        for(auto i : s) {
            dp[1][i - 'a'] = 1;
        }


        for(long long i = 2; i <= n; i++) {
            for(auto j : s) {
                if(j == 'a') { //curr
                    dp[i][j - 'a'] = dp[i - 1]['u' - 'a']%MOD + dp[i - 1]['e' - 'a']%MOD + dp[i - 1]['i' - 'a']%MOD;
                    dp[i][j  - 'a'] %= MOD;


                }
                else if(j == 'e') {
                    dp[i][j - 'a'] = dp[i - 1]['a' - 'a']%MOD + dp[i - 1]['i' - 'a']%MOD;
                    dp[i][j  - 'a'] %= MOD;

                }
                else if(j == 'i') {
                    dp[i][j - 'a'] = dp[i - 1]['e' - 'a']%MOD + dp[i - 1]['o' - 'a']%MOD;
                    dp[i][j  - 'a'] %= MOD;
                }
                else if(j == 'o') {
                    dp[i][j - 'a'] =  dp[i - 1]['i' - 'a']%MOD;
                    dp[i][j  - 'a'] %= MOD;
                }
                else if(j == 'u') {
                    dp[i][j - 'a']= dp[i - 1]['i' - 'a']%MOD + dp[i - 1]['o' - 'a']%MOD;
                    dp[i][j  - 'a'] %= MOD;
                }
            }
        }


        long long ans = 0;
        for(auto j : s) {
            ans += dp[n][j - 'a']%MOD;
            ans %= MOD;
        }
        return ans;


    }
};
