long long dp[100005];
const int MOD = 1e9 + 7;
class Solution {
public:
    long long helper(int low, int high, int zero, int one, int len){
        if(len > high)return 0;
        if(dp[len] != -1)return dp[len];
        long long ans = 0;
        if(len >= low)ans++;
        ans += helper(low, high, zero, one, len + one);
        ans += helper(low, high, zero, one, len + zero);
        return dp[len] = ans%MOD;
    }
    int countGoodStrings(int low, int high, int zero, int one) {
        memset(dp, -1, sizeof dp);
        return helper(low, high, zero, one, 0);
    }
};
