class Solution {
public:

    int wordCount(vector<string>& s, vector<string>& t) {
        for(int i=0;i<s.size();i++) sort(s[i].begin(),s[i].end());
          for(int i=0;i<t.size();i++) sort(t[i].begin(),t[i].end());
        unordered_map<string,int> umap;
        for(auto iter:s){ umap[iter]++;}
        int count=0;
        // cout<<endl;
        for(auto iter:t){
            // cout<<iter<<" ";
            for(int i=0;i<iter.size();i++){
                string k="";
                if(i-1>=0)
                    k+=iter.substr(0,i-1-0+1);
                if(i+1<iter.size())
                    k+=iter.substr(i+1,iter.size()-1-i+1);
                // cout<<k<<endl;
                if(umap.count(k)){ count++; break;}
            }
        }
        return count;


    }
};
