class Solution {
public:
    vector<int> countBits(int n) {
        vector<int> dp(n+1);
        if(n == 0) return {0};
        dp[0] = 0;
        dp[1] = 1;
        long long pow = 1;
        for(int i=2;i<=n;i++){
            if((int)(log2(i)) == log2(i)) pow *= 2;
            dp[i] = 1 + dp[i - pow];
        }
        return dp;        
    }
};
