class Solution {
public:
    int prefixCount(vector<string>& words, string pref) {
        int size = pref.size();
        int cnt  =0;
        for(auto i : words) {
            if(i.size() >= size and i.substr(0, size) == pref) {
                cnt++;
            }
        }
        return cnt;
    }
};
