class Solution {
public:
    static bool comp(vector<int> &a, vector<int> &b) {
        return a[1]  < b[1];
    }
    int scheduleCourse(vector<vector<int>>& courses) {
        sort(courses.begin(), courses.end(), comp);
        int days = 0;
        priority_queue<int> q;
        for(auto i : courses) {
            int duration = i[0];
            int lastDay = i[1];
            days += duration;
            q.push(duration);
            while(!q.empty() and days > lastDay) {
                days -= q.top();
                q.pop();
            }

        }
       return q.size();


    }
};
