class Solution {
public:
    map<string, bool> vis;
    string ans;
    bool dfs(string temp, int len, int k, int n) {
        if(len == 0) {
            ans = temp;
           return true;
        }
        for(int i = 0; i < k; i++) {
            if(temp.size() < n - 1) 
            {
                bool ok = dfs(temp + to_string(i), len - 1, k, n);
                if(ok) {
                    return true;
                }
            }
            else 
            {
                string p = temp;
                p.push_back(char(i + '0'));
                string sub  = p.substr(p.size() - n);
                if(vis[sub] == false) {
                    vis[sub] = true;
                    bool ok = dfs(temp + to_string(i), len - 1, k, n);
                    if(ok) {
                        return true;
                    }
                    vis[sub] = false;
                }
            }
        }
        return false;
    }

    string crackSafe(int n, int k) {

        if(n == 1) {
            string ans;
            for(int i = 0; i < k; i++) {
                ans.push_back(char(i + '0'));
            }
            return ans;
        }
        int len = 1;
        for(int i = 0; i < n; i++) {
            len *= k;
        }

        len += (n - 1);
        dfs("", len, k, n);
        return ans;




    }
};
