class Solution {
public:
    string customSortString(string order, string s) {
        map<char,int> mp;
        for(auto it:s){
            mp[it]++;
        }
        string ans="";
        for(auto it:order){
           //auto
           if(mp.find(it)!=mp.end()){
               auto temp = mp.find(it);
               int count = temp->second;
               string ss(count,it);
               ans+=ss;
               mp.erase(it);


           }
        }
        for(auto it:mp){
            string sss(it.second,it.first);
            ans+=sss;
        }
        return ans;

    }
};
